import { View, Text, ScrollView } from 'react-native'
import React ,{useEffect,useState} from 'react'
import { ArrowRightIcon } from 'react-native-heroicons/outline'
import ResturantsCard from './ResturantsCard'
import sanityClient from '../sanity'

const FeaturedRows = ({id,title, description}) => {
const [Restaurant, setRestaurant] = useState([]);
useEffect(() => {
  sanityClient.fetch(`
  *[_type == "featured" && _id==$id]{
    ...,
    restaurants[]->{
      ...,
      dishes[]->,
      type->{
        name
      }
    },
  }[0] `,{id}).then((data)=>{
    setRestaurant(data?.restaurants)
  });

 
}, []);

console.log(Restaurant);

  return (
    <View>
     <View className="mt-4 flex-row items-center justify-between px-4">
        <Text className="font-bold text-lg">{title}</Text>
        <ArrowRightIcon color="#00CCBB"/>
     </View>
     <Text className="text-xs text-gray-500 px-4 ">{description}</Text>
     <ScrollView horizontal contentContainerStyle={{
            paddingHorizontal:15,}}
        showsHorizontalScrollIndicator={false} className="pt-4">
            {/*resturant cards */}

            {Restaurant?.map((restro)=>(
            <ResturantsCard 
            key={restro._id}
            id={restro._id}
            imgUrl={restro.image}
            title={restro.name}
            rating={restro.rating}
            genre={restro.type?.name}
            address={restro.address}
            short_description={restro.short_description}
            dishes={restro.dishes}
            long={restro.long}
            lat={restro.lat}
            />))}

             <ResturantsCard id={123}
            imgUrl="https://www.simplyrecipes.com/thmb/1KOEQ0SPZNXwU0pUXUDdAm6Z7xo=/2001x2001/smart/filters:no_upscale()/Simply-Recipes-Homemade-Pizza-Dough-Lead-Shot-1c-c2b1885d27d4481c9cfe6f6286a64342.jpg"
            title="pizza"
            rating={4.5}
            genre="Italian"
            address="123 main st"
            short_description="this is a test description"
            dishes={{}}
            long={20}
            lat={0}
            />
            
            

     </ScrollView>
    </View>
  )
}

export default FeaturedRows
