import { View, Text, ScrollView } from 'react-native'
import React from 'react'
import CategoriesCard from './CategoriesCard'

const Categories = () => {
  return (
    <View>
        <ScrollView horizontal contentContainerStyle={{
            paddingHorizontal:15,
            paddingTop:10,
        }}
        showsHorizontalScrollIndicator={false}>

            {/*categories card  */}
            <CategoriesCard imgUrl="https://previews.123rf.com/images/blankstock/blankstock2012/blankstock201204047/160890350-best-offer-banner-yellow-background-with-offer-message-discount-sticker-shape-sale-coupon-bubble-ico.jpg" title="Offers"/>
            <CategoriesCard imgUrl="https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fstatic.onecms.io%2Fwp-content%2Fuploads%2Fsites%2F9%2F2021%2F06%2F15%2Fmozzarella-pizza-margherita-FT-RECIPE0621.jpg&q=60" title="Pizza"/>
            <CategoriesCard imgUrl="https://assets.gqindia.com/photos/5e0dd5cdbd97c00008f3a19d/master/pass/top-image.jpg" title="Italian"/>
            <CategoriesCard imgUrl="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRcmun87MhkqPXwWS8R9PJ_exGIEsVnPKPHGw&usqp=CAU" title="Chinese"/>
            <CategoriesCard imgUrl="https://theculturetrip.com/wp-content/uploads/2020/04/2bcpa1n.jpg" title="Thai"/>
            <CategoriesCard imgUrl="https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/thali-indian-1296x728-header.jpg?w=1155&h=1528" title="Indian"/>

        </ScrollView>
      
    </View>
  )
}

export default Categories
